import {BEGIN_GET_USERDATA, 
    GET_USERDATA_OK, 
    GET_USERDATA_ERROR,
    COMIENZA_LOGIN,
    LOGIN_EXITOSO,
    LOGIN_ERROR
} from '../types/index';

const inisialState = {

    error: null,
    datosusuario: null,
    loading: false
}

export default (state = inisialState, action) =>{

    switch(action.type){
        case BEGIN_GET_USERDATA:
            return{
                ...state,
                loading: true,
            }
        case GET_USERDATA_OK:
            return{
                ...state,
                loading: false,
                datosusuario: action.payload

            }
        
        default: return state;
    }
}
