import React, {useState} from 'react';
import {getUserDataAction, loginUserAction} from '../actions/userAction';
import {useDispatch, useSelector} from 'react-redux';



const Login = () => {


    const dispatch = useDispatch();

    const loginuser = (data) => dispatch( loginUserAction(data) );


    const [userdata, saveUserData] = useState({

        email: '',
        password: ''
    })
    const handlChange = e =>{

        saveUserData({
            ...userdata,
            [e.target.name]: e.target.value
        })

    }
    const {email, password} = userdata;
    const handleSubtmit = e =>{

        e.preventDefault();

        // validate the form


        // send to de action 
        loginuser({email, password});

        // reset form
    }
    return ( 

            <div className="contenedor">
                <div className="contenedor-formulario">
                        <form
                            onSubmit={handleSubtmit}
                        >
                            <div className="row">
                                <div className="col-md-2 form-group">
                                     <label className="form-group">Email</label>
                                </div>
                                <div className="col-md-10 form-group">
                                            <input
                                                type='text'
                                                className="form-control"
                                                name="email"
                                                onChange={handlChange}
                                            />
                                </div> 
                                <div className="col-md-2 form-group">
                                     <label className="form-group">Password</label>
                                </div>
                                <div className="col-md-10 form-group">
                                            <input
                                                type='password'
                                                className="form-control"
                                                name="password"
                                                onChange={handlChange}
                                            />
                                </div> 
                                <div className="col-md-12 form-group">
                                            <button className="btn btn-primary btn-block">Login</button>    
                                </div> 
                            </div>
                        </form>
                </div>
            </div>

     );
}
 
export default Login;
