import {BEGIN_GET_USERDATA, 
        GET_USERDATA_OK, 
        GET_USERDATA_ERROR,
        COMIENZA_LOGIN,
        LOGIN_EXITOSO,
        LOGIN_ERROR,
        OCULTAR_ERROR,
        MOSTRAR_ERROR
} from '../types/index';
import clienteAxios from '../config/axios';
import tokenAuth from '../config/tokenAuth';

export function mostrarErrorAction(msg, categoria){
        const alerta = {

                msg,
                categoria                
        }
        return(dispatch)=>{

                dispatch( mostraError(alerta) );               
        }
}
const mostraError = (alerta) => ({

        type: MOSTRAR_ERROR,
        payload: alerta
})

export function ocultarAlertAction(){

        return(dispatch)=>{

                dispatch( ocultarAlerta() ); 
        }
}
const ocultarAlerta = () => ({

        type: OCULTAR_ERROR
})

export function loginUsuarioaction(datos){


        return async(dispatch)=>{
                
                dispatch( comienzaLogin() );
                try {
                        const resultado = await clienteAxios.post('/api/auth', datos);
                        // console.log(resultado);
                        dispatch( loginExitoso(resultado.data) );  
                        obtieneUsuarioAction();                    
                } catch (error) {
                        console.log(error.response)
                }
        }
}
const comienzaLogin = () => ({

        type: COMIENZA_LOGIN
})
const loginExitoso = (token) => ({

        type: LOGIN_EXITOSO,
        payload: token
})
