import React from 'react';
import {useSelector, useDispatch} from 'react-redux';
import {mostrarUsuarioAction} from '../actions/userAction';

const Sidebar = () => {


    const dispatch = useDispatch();
    const mostrarusuarios = useSelector(state => state.userdata.mostrarusuarios);
    const mostrarAdministraUsuarios = () => dispatch( mostrarUsuarioAction() );
    

const handleClick = usuario =>{
   
    if(!usuario){

        usuario = true;
        mostrarAdministraUsuarios();        
    }

}


    return ( 
        
            <ul className="nav flex-column">
                <li className="nav-item">
                    <a className="nav-link" href="!#">Crear Usuarios</a>
                </li>
                <li className="nav-item">
                    <a className="nav-link" href="!#">Actividades</a>
                </li>
                <li className="nav-item">
                    <a className="nav-link" href="!#">Peticiones</a>
                </li>
                <li className="nav-item">
                    <button 
                    type="button"   
                    onClick={e=>handleClick(mostrarusuarios)}                 
                    className="btn btn-block">Administrar Usuarios</button> 
                </li>
            </ul>
      


     );
}
 
export default Sidebar;
