import {BEGIN_GET_USERDATA, 
        GET_USERDATA_OK, 
        GET_USERDATA_ERROR,
        COMIENZA_LOGIN,
        LOGIN_EXITOSO,
        LOGIN_ERROR,
        OCULTAR_ERROR,
        MOSTRAR_ERROR
} from '../types/index';
import clienteAxios from '../config/axios';


export function mostrarErrorAction(msg, categoria){
        const alerta = {

                msg,
                categoria
                
        }
        return(dispatch)=>{

                dispatch( mostraError(alerta) );
                
                
        }


}
const mostraError = (alerta) => ({

        type: MOSTRAR_ERROR,
        payload: alerta
})
