import {BEGIN_GET_USERDATA, 
    GET_USERDATA_OK, 
    GET_USERDATA_ERROR,
    COMIENZA_LOGIN,
    LOGIN_EXITOSO,
    LOGIN_ERROR,
    MOSTRAR_ERROR, 
    OCULTAR_ERROR
} from '../types/index';

const inisialState = {

    error: null,
    datosusuario: null,
    loading: false,
    mensaje: null,
    token: null,
    mostrar: null
}

export default (state = inisialState, action) =>{

    switch(action.type){
        case MOSTRAR_ERROR:
            return{
                ...state,
                mensaje: action.payload
            }
        case OCULTAR_ERROR:
            return{
                ...state,
                mensaje: null
            }
        case LOGIN_EXITOSO:
            localStorage.setItem('token', action.payload.token)
            return{
                ...state,
                token: action.payload.token

            }
        case GET_USERDATA_OK:
            return{
                ...state,
                datosusuario: action.payload
            }
        default: return state;
    }
}
