import {BEGIN_GET_USERDATA, 
        GET_USERDATA_OK, 
        GET_USERDATA_ERROR,
        COMIENZA_LOGIN,
        LOGIN_EXITOSO,
        LOGIN_ERROR,
        OCULTAR_ERROR,
        MOSTRAR_ERROR
} from '../types/index';
import clienteAxios from '../config/axios';


export function mostrarErrorAction(){
       
        return(dispatch)=>{

                dispatch( mostraError() );
        }


}
const mostraError = (msg, categoria) => ({

        type: MOSTRAR_ERROR,
        payload: {
                msg, categoria
        }

}, setTimeout((dispatch)=>{
        
        dispatch( ocultarError() );
        const ocultarError = () => ({

                type: OCULTAR_ERROR
        })
}, 3000))
