import React from 'react';
import {useSelector} from 'react-redux';

const Sidebar = () => {

    const mostrarusurios = useSelector(state => state.userdata.mostrarusuarios);
    
const handleClick = mostrar =>{

    if(!mostrar){

        mostrar = true;
    }else{

        mostrar = false;
    }

}


    return ( 
        
            <ul className="nav flex-column">
                <li className="nav-item">
                    <a className="nav-link" href="!#">Crear Usuarios</a>
                </li>
                <li className="nav-item">
                    <a className="nav-link" href="!#">Actividades</a>
                </li>
                <li className="nav-item">
                    <a className="nav-link" href="!#">Peticiones</a>
                </li>
                <li className="nav-item">
                    <button 
                    type="button"   
                    onClick={handleClick(mostrar)}                 
                    className="btn btn-block">Administrar Usuarios</button> 
                </li>
            </ul>
      


     );
}
 
export default Sidebar;
