import React from 'react';
import {useSelector, useDispatch} from 'react-redux';
import {mostrarUsuarioAction, mostrarCreaUsuariosAction} from '../actions/userAction';

const Sidebar = () => {


    const dispatch = useDispatch();
    const mostrarusuarios = useSelector(state => state.userdata.mostrarusuarios);
    const mostrarcreausuarios = useSelector( state => state.userdata.mostrarcreausuarios);

    const mostrarAdministraUsuarios = () => dispatch( mostrarUsuarioAction() );
    const mostrarCreaUsuario = () => dispatch( mostrarCreaUsuariosAction() );
    

const handleClick = usuario =>{
   
    if(!usuario){

        usuario = true;
        mostrarAdministraUsuarios();        
    }

}
const handleClickCrea = e =>{

    if(!mostrarcreausuarios){

        mostrarcreausuarios = true
        mostrarCreaUsuario();
    }

}


    return ( 
        
            <ul className="nav flex-column">
                <li className="nav-item">
                    <button 
                        type="button"   
                        onClick={e=>handleClickCrea(mostrarcreausuarios)}                 
                        className="btn btn-block">Crear Usuarios</button> 
                </li>
                <li className="nav-item">
                    <a className="nav-link" href="!#">Actividades</a>
                </li>
                <li className="nav-item">
                    <a className="nav-link" href="!#">Peticiones</a>
                </li>
                <li className="nav-item">
                    <button 
                    type="button"   
                    onClick={e=>handleClick(mostrarusuarios)}                 
                    className="btn btn-block">Administrar Usuarios</button> 
                </li>
            </ul>
      


     );
}
 
export default Sidebar;
